","keccak256":"0x808d3a486826e7f119671174a77896c6e23578e691625350dbb3e9f74a0d5137","license":"AGPLv3"},"@superfluid-finance/ethereum-contracts/contracts/interfaces/tokens/TokenInfo.sol":{"content":"// SPDX-License-Identifier: AGPLv3
pragma solidity >= 0.5.0;

/**
 * @dev ERC20 token info interface
 *
 * NOTE: ERC20 standard interface does not specify these functions, but
 * often the token implementations have them.
 *
 */
interface TokenInfo {
    /**
     * @dev Returns the name of the token.
     */
    function name() external view returns (string memory);

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
     * name.
     */
    function symbol() external view returns (string memory);

    /**
     * @dev Returns the number of decimals used to get its user representation.
     * For example, if `decimals` equals `2`, a balance of `505` tokens should
     * be displayed to a user as `5,05` (`505 / 10 ** 2`).
     *
     * Tokens usually opt for a value of 18, imitating the relationship between
     * Ether and Wei. This is the value {ERC20} uses, unless {_setupDecimals} is
     * called.
     *
     * NOTE: This information is only used for _display_ purposes: it in
     * no way affects any of the arithmetic of the contract, including
     * {IERC20-balanceOf} and {IERC20-transfer}.
     */
    function decimals() external view returns (uint8);
}
","keccak256":"0x4dee21b8f64edbfe811a300d31f529b5367d1186d69b8b2b8dc3373be7a108b0","license":"AGPLv3"},"contracts/BrightIDStream.sol":{"content":"// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

import '@openzeppelin/contracts/token/ERC20/SafeERC20.sol';
import {ERC721} from "@openzeppelin/contracts/token/ERC721/ERC721.sol";
import {Ownable} from "@openzeppelin/contracts/access/Ownable.sol";
import {ISuperfluid, ISuperToken, ISuperApp} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/superfluid/ISuperfluid.sol";
import {IConstantFlowAgreementV1} from "@superfluid-finance/ethereum-contracts/contracts/interfaces/agreements/IConstantFlowAgreementV1.sol";



contract BrightIDStream is ERC721, Ownable {
    ISuperfluid private _host; // host
    IConstantFlowAgreementV1 private _cfa; // the stored constant flow agreement class address
    ISuperToken public _acceptedToken; // accepted token
    mapping(uint256 => int96) public flowRates;

    //IERC20 public sToken;  //address for superToken initialized

	int96 public tokenFlowRate = 385802469135800;

	//uint256 public cost = .01 ether;  
	uint256 public maxSupply = 10;  // max supply of tokens
	uint256 public tokenCounter;

	string public nftBaseURI = 'ipfs://QmWaW7fAEcRjvr25cvsFrUbWZGDnq6pVmnDGMs8uuV9iYW/1.json';

	bool public paused = false;

    constructor(
        string memory _name,
        string memory _symbol,
        ISuperfluid host,
        IConstantFlowAgreementV1 cfa,
        ISuperToken acceptedToken
    ) ERC721(_name, _symbol) {
        _host = host;
        _cfa = cfa;
        _acceptedToken = acceptedToken;


        assert(address(_host) != address(0));
        assert(address(_cfa) != address(0));
        assert(address(_acceptedToken) != address(0));

        //set the uri - this can be a constructor arg
		_setBaseURI(nftBaseURI);
		//sToken = IERC20(acceptedToken);
    }

    event NFTIssued(uint256 tokenId, address receiver, int96 flowRate);
    event TransferSent(address _from, address _destAddr, uint _amount);

    /*
    // Minter
    function mintNFT() public onlyOwner {
        require(!paused, "the contract is paused");
		require(msg.sender != address(this), 'Issue to a new address');
		require(tokenCounter <=  maxSupply, "max NFT limit exceeded");
        //require(msg.value >= cost, "insufficient funds");

        tokenCounter = tokenCounter + 1;				// calculatedFlowRate = Math.floor(monthlyAmount / 3600 / 24 / 30)  **Monthly amount in Gwei**
														// Flow rate for FTTx 38580246913580  / Flow Rate for Dai(dollar) 3858024691358
		flowRates[tokenCounter] = tokenFlowRate;     // <<<<<<<<<<<<<<<<<<   Flow rate hardcoded to 10 tokens.. can be set to a global varaible
		emit NFTIssued(tokenCounter, msg.sender, flowRates[tokenCounter]);
		
		_safeMint(msg.sender, tokenCounter);
    } */
    
    function _issueNFT(address receiver, int96 flowRate) public onlyOwner {
        require(!paused, "the contract is paused");
        require(receiver != address(this), "Issue to a new address");
        require(flowRate > 0, "flowRate must be positive!");

        tokenCounter = tokenCounter + 1;				// calculatedFlowRate = Math.floor(monthlyAmount / 3600 / 24 / 30)  **Monthly amount in Gwei**
														// Flow rate for FTTx 38580246913580  / Flow Rate for Dai(dollar) 3858024691358
		flowRates[tokenCounter] = tokenFlowRate;     // <<<<<<<<<<<<<<<<<<   Flow rate hardcoded to 10 tokens.. can be set to a global varaible
		emit NFTIssued(tokenCounter, msg.sender, flowRates[tokenCounter]);

        _safeMint(receiver, tokenCounter);
    }
    

    function burnNFT(uint256 tokenId) external onlyOwner exists(tokenId) {
        require(!paused, "the contract is paused");
        address receiver = ownerOf(tokenId);

        int96 rate = flowRates[tokenId];
        delete flowRates[tokenId];
        _burn(tokenId);
        //deletes flow to previous holder of nft & receiver of stream after it is burned

        //we will reduce flow of owner of NFT by total flow rate that was being sent to owner of this token
        _reduceFlow(receiver, rate);
    }

    //Everytime token is transfered, the stream is moved to owner
    function _beforeTokenTransfer(
        address oldReceiver,
        address newReceiver,
        uint256 tokenId
    ) internal override {
        //blocks transfers to superApps - done for simplicity, but you could support super apps in a new version!
        require(
            !_host.isApp(ISuperApp(newReceiver)) ||
                newReceiver == address(this),
            "New receiver can not be a superApp"
        );

        // @dev delete flowRate of this token from old receiver
        // ignores minting case
        _reduceFlow(oldReceiver, flowRates[tokenId]);
        // @dev create flowRate of this token to new receiver
        // ignores return-to-issuer case
        _increaseFlow(newReceiver, flowRates[tokenId]);
    }

    /**************************************************************************
     * Modifiers
     *************************************************************************/

    modifier exists(uint256 tokenId) {
        require(_exists(tokenId), "token doesn't exist or has been burnt");
        _;
    }

    /**************************************************************************
     * Library
     *************************************************************************/
    //this will reduce the flow or delete it
    function _reduceFlow(address to, int96 flowRate) internal {
        if (to == address(this)) return;

        (, int96 outFlowRate, , ) = _cfa.getFlow(
            _acceptedToken,
            address(this),
            to
        );

        if (outFlowRate == flowRate) {
            _deleteFlow(address(this), to);
        } else if (outFlowRate > flowRate) {
            // reduce the outflow by flowRate;
            // shouldn't overflow, because we just checked that it was bigger.
            _updateFlow(to, outFlowRate - flowRate);
        }
        // won't do anything if outFlowRate < flowRate
    }

    //this will increase the flow or create it
    function _increaseFlow(address to, int96 flowRate) internal {
        (, int96 outFlowRate, , ) = _cfa.getFlow(
            _acceptedToken,
            address(this),
            to
        ); //returns 0 if stream doesn't exist
        if (outFlowRate == 0) {
            _createFlow(to, flowRate);
        } else {
            // increase the outflow by flowRates[tokenId]
            _updateFlow(to, outFlowRate + flowRate);
        }
    }

    function _createFlow(address to, int96 flowRate) internal {
        if (to == address(this) || to == address(0)) return;
        _host.callAgreement(
            _cfa,
            abi.encodeWithSelector(
                _cfa.createFlow.selector,
                _acceptedToken,
                to,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x"
        );
    }

    function _updateFlow(address to, int96 flowRate) internal {
        if (to == address(this) || to == address(0)) return;
        _host.callAgreement(
            _cfa,
            abi.encodeWithSelector(
                _cfa.updateFlow.selector,
                _acceptedToken,
                to,
                flowRate,
                new bytes(0) // placeholder
            ),
            "0x"
        );
    }

    function _deleteFlow(address from, address to) internal {
        _host.callAgreement(
            _cfa,
            abi.encodeWithSelector(
                _cfa.deleteFlow.selector,
                _acceptedToken,
                from,
                to,
                new bytes(0) // placeholder
            ),
            "0x"
        );
    }

    function tokenURI(uint256 tokenId) public view virtual override returns (string memory) {
        require(_exists(tokenId), "ERC721Metadata: URI query for nonexistent token");

        string memory currentBaseURI = baseURI();
        return bytes(currentBaseURI).length > 0
         ? string(abi.encodePacked(currentBaseURI))
            : "";
    }

    // Only Owner functions
    /*
    function setCost(uint256 _newCost) public onlyOwner() {
    	cost = _newCost;
  	}*/
	
	function setNFTBaseURI(string memory _nftBaseURI) public onlyOwner() {
    	nftBaseURI = _nftBaseURI;
  	}

	function changeFlowRate(int96 _flowRate ) public onlyOwner() {
    	tokenFlowRate = _flowRate;
  	}

	function pause(bool _state) public onlyOwner {
    	paused = _state;
  	}

	function changeMaxSupply(uint256 _newmaxSupplyAmount) public onlyOwner() {
    	maxSupply = _newmaxSupplyAmount;
  	}

    function changeSuperToken(address acceptedToken) public onlyOwner() {
    	_acceptedToken = ISuperToken(acceptedToken);
  	}
    //function to pull out token
    function withdrawToken(IERC20 token) public onlyOwner {
        require(token.transfer(msg.sender, token.balanceOf(address(this))), "Unable to transfer");
    }

    function withdraw() public payable onlyOwner {
        (bool success, ) = payable(msg.sender).call{value: address(this).balance}("");
        require(success);
    }


}","keccak256":"0x4ba5c61e144aedf5549e647f6c95b51f5ffdb4f1263a9c0b20e5c51cd518c0dd","license":"MIT"}},"version":1}
